sp_widget.client_script:
api.controller=function($scope,$rootScope) {
	/* widget controller */
	var c = this;

	c.updateNote = function(actionType){
		c.server.get({
			action:actionType,
			noteID: c.data.noteID,
			title:c.data.title,
			note: c.data.note
		}).then(function(r){
		});

	}
	$rootScope.$on('selectNote', function(event,data) { 
		c.server.get({
			action:'getNote',
			noteID: $rootScope.noteID
		}).then(function(r){
			c.data.title= r.data.notes.title;
			c.data.note = r.data.notes.note;
			c.data.noteID = r.data.notes.sys_id;
		});
	});

};

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	if(input && input.noteID){
		var note= new GlideRecord('x_snc_createnotes_note');
		if(note.get(input.noteID)){
			if(input.action=='getNote'){
				data.notes = {};
				$sp.getRecordDisplayValues(data.notes, note, "number,title, note, sys_id");
			}else if(input.action=='updateNote'){
				note.title= input.title;
				note.note= input.note;
				note.update();
			}
		}
	}

	/*
	var note= new GlideRecord('x_snc_createnotes_note');
	note.query();
	data.notes=[];
	while(note.next()){
		var obj = {};
		$sp.getRecordDisplayValues(obj, note, "title, note, sys_id");
		data.notes.push(obj);
	}
	*/
})();
